/*设置部门人数*/
update Department set Depar_number = 
    (select count(*)
    from Student, Class
    where Student.Class_id = Class.Class_id
    and Depar_id = Department.Depar_id)
    +
    (select count(*)
    from Teacher
    where Depar_id = Department.Depar_id);

/*设置班级人数*/
update class set Class_number = 
(select count(*)
from Student
where Student.Class_id = Class.Class_id);